function SheetExists(sheetName)
{
	if(_inlineScripts.state.sessionState.eventCrafter.content.indexOf(sheetName) !== -1)
	{
		return true;
	}

	return false;
}

function CategoryExistsInActiveSheet(categoryName)
{
	const activeSheetName = _inlineScripts.state.sessionState.eventCrafter.activeSheet;
	if(activeSheetName === &quot;&quot;)
	{
		return false;
	}

	const activeSheet = _inlineScripts.state.sessionState.eventCrafter.lists[activeSheetName];
	if(activeSheet.content.indexOf(categoryName) !== -1)
	{
		return true;
	}

	return false;
}

function GetUniquesOffset(category, roll)
{
	let uniqueOffset = 0;
	category.uniqueIndicesRemoved.forEach(
		(elem, index, array) =&gt; 
		{
			if(elem &lt;= roll)
			{
				uniqueOffset++;
			}
		}
	);

	return uniqueOffset;
}

function CheckRolledElement(elem, rollWithOffset)
{
	if(elem.unique === 1)
	{
		elem.unique = 2;
		category.uniqueIndicesRemoved.push(rollWithOffset);
	}
}

// if message contains EC_ERROR, it is splitted away and the error text extracted
function CheckAndReturnError(message)
{
	if(message.length &gt; 1)
	{
		return { result: true, error: message[0] };
	}
	return { result: false };
}

const CategoryDefinitionPopup =
{
	// Name, rand/struct (radio buttons), dropdown (concluding type)
	buttons: [ &quot;Ok&quot;, &quot;Cancel&quot; ],
	onOpen: function(data, parent, firstButton, SettingType)
	{
		let nameDiv = document.createElement(&quot;div&quot;);
		let radioDiv = document.createElement(&quot;div&quot;);
		let randomDiv = document.createElement(&quot;div&quot;);
		let structuredDiv = document.createElement(&quot;div&quot;);
		let concludingDiv = document.createElement(&quot;div&quot;);

		// label
		let nameInputLabel = document.createElement(&quot;label&quot;);
		nameInputLabel.htmlFor = &quot;EC_nameInput&quot;;
		nameInputLabel.innerText = &quot;Category Name&quot;;
		let nameInput = document.createElement(&quot;input&quot;);
		nameInput.id = &quot;EC_nameInput&quot;;
		nameInput.type = &quot;text&quot;;
		nameDiv.appendChild(nameInputLabel);
		nameInputLabel.appendChild(nameInput);

		// Radio Section
		radioDiv.appendChild(randomDiv);
		radioDiv.appendChild(structuredDiv);

		let concludingElement = document.createElement(&quot;select&quot;);
		concludingElement.id = &quot;EC_concludingElement&quot;;
		concludingElement.appendChild(new Option(&quot;Non-Concluding&quot;, &quot;nc&quot;));
		concludingElement.appendChild(new Option(&quot;Concluding&quot;, &quot;c&quot;));
		concludingElement.appendChild(new Option(&quot;Concluding, Short&quot;, &quot;cs&quot;));
		concludingElement.selectedIndex = data.defaultValue || 0;
		concludingElement.classList.add(&quot;dropdown&quot;);
		// label
		let concludingElementLabel = document.createElement(&quot;label&quot;);
		concludingElementLabel.htmlFor = &quot;EC_concludingElement&quot;;
		concludingElementLabel.innerText = &quot;Concluding Type&quot;;

		concludingDiv.appendChild(concludingElementLabel);
		concludingDiv.appendChild(concludingElement);
		concludingDiv.style[&quot;margin-bottom&quot;] = &quot;1.5em&quot;;
		concludingDiv.style[&quot;margin-top&quot;] = &quot;1.5em&quot;;
		concludingDiv.style[&quot;display&quot;] = &quot;none&quot;;

		let randomRadioInput = document.createElement(&quot;input&quot;);
		randomRadioInput.type = &quot;radio&quot;;
		randomRadioInput.id = &quot;EC_randomRadioInput&quot;;
		randomRadioInput.name = &quot;randomType&quot;;
		randomRadioInput.value = &quot;random&quot;;
		randomRadioInput.onclick = (e) =&gt;
		{
			concludingDiv.style[&quot;display&quot;] = &quot;block&quot;;
			data.resultUi.random = true;
			data.resultUi.concludingType = concludingElement;
		};
		// label
		let randomRadioLabel = document.createElement(&quot;label&quot;);
		randomRadioLabel.htmlFor = &quot;EC_randomRadioInput&quot;;
		randomRadioLabel.innerText = &quot;Random&quot;;

		randomDiv.appendChild(randomRadioInput);
		randomDiv.appendChild(randomRadioLabel);
		randomRadioInput.style[&quot;box-sizing&quot;] = &quot;border-box&quot;;
		randomRadioInput.style[&quot;width&quot;] = &quot;50%&quot;;
		randomRadioInput.style[&quot;margin&quot;] = &quot;auto&quot;;

		let structuredRadioInput = document.createElement(&quot;input&quot;);
		structuredRadioInput.type = &quot;radio&quot;;
		structuredRadioInput.id = &quot;EC_structuredRadioInput&quot;;
		structuredRadioInput.name = &quot;randomType&quot;;
		structuredRadioInput.value = &quot;structured&quot;;
		structuredRadioInput.checked = true;
		structuredRadioInput.onclick = (e) =&gt;
		{
			concludingDiv.style[&quot;display&quot;] = &quot;none&quot;;
			data.resultUi.random = false;
			data.resultUi.concludingType = null;
		};
		// label
		let structuredRadioLabel = document.createElement(&quot;label&quot;);
		structuredRadioLabel.htmlFor = &quot;EC_structuredRadioInput&quot;;
		structuredRadioLabel.innerText = &quot;Structured&quot;;
		structuredDiv.appendChild(structuredRadioInput);
		structuredDiv.appendChild(structuredRadioLabel);
		structuredRadioInput.style[&quot;box-sizing&quot;] = &quot;border-box&quot;;
		structuredRadioInput.style[&quot;width&quot;] = &quot;50%&quot;;
		structuredRadioInput.style[&quot;margin&quot;] = &quot;auto&quot;;

		parent.appendChild(nameDiv);
		parent.appendChild(document.createElement(&quot;br&quot;));
		parent.appendChild(radioDiv);
		parent.appendChild(concludingDiv);

		parent.addEventListener(&quot;keypress&quot;, (e) =&gt;
		{
			if (e.key === &quot;Enter&quot;)
			{
				firstButton.click();
			}
		});

		data.resultUi = {name: nameInput, random: false, concludingType: null};
	},
	onClose: function(data, resolveFnc, buttonText)
	{
		if (buttonText === &quot;Ok&quot;)
		{
			let result = { name: data.resultUi.name.value, random: false };
			if(data.resultUi.concludingType)
			{
				result.concludingType = data.resultUi.concludingType.value;
				result.random = true;
			}
			resolveFnc(result);
		}
		else
		{
			resolveFnc(null);
		}
	}
};

const PickResultDefinitionPopup =
{
